const ObjectId = require("mongodb").ObjectID;
import {
  encryptpassword,
  decryptPassword,
  generateJwtTokenFn,
  generateRandom,
} from "../../utilities/universal";
const dbService = require("../../utilities/dbService");

/*************************** addContractor ***************************/
const addUser = async (req, res) => {
  console.log("req service =>", req.body);
  const { email } = req.body;
  console.log("email =>", email);
  let customerData = await dbService.findOneRecord("userModel", {
    email: email,
  });
  console.log("customerData =>", customerData);
  if (customerData) {
    throw new Error("Email Address Already Exists!");
  } else {
    console.log("req.body.password =>", req.body.password);
    let password = await encryptpassword(req.body.password);
    console.log("encryptpassword  password =>", password);
    console.log("still we have req.body.password =>", req.body.password);
    req.body.password = password;
    console.log("after we have req.body.password =>", req.body.password);
    console.log("req.body->", req.body);

    let userData = await dbService.createOneRecord("userModel", req.body);

    if (userData) {
      let token = await generateJwtTokenFn({ userId: userData._id });
      let updateData = {
        $push: {
          loginToken: {
            token: token,
          },
        },
      };

      let data = await dbService.findOneAndUpdateRecord(
        "userModel",
        { _id: userData._id },
        updateData,
        { new: true }
      );

      const options = {
        expires: new Date(Date.now() + 10 * 24 * 60 * 60 * 1000),
        httpOnly: true,
      };
      res.cookie("token", token, options);

      return data;
    } 
  }
};

