    "contractorModel",
    where,
    sort,
    skiprecord,
    limit
  );
  var result = JSON.parse(JSON.stringify(results));
  if (result.length !== 0) {
    for (let i = 0; i < result.length; i++) {
      Object.assign(result[i], {
        fullName: result[i].firstName + " " + result[i].lastName,
      });
    }
  }
  return {
    status: "Success",
    message: "all Contractor fetched successfully.",
    items: result,
    page: page,
    count: totalrecord,
    limit: limit,
  };
